import React, { createContext, useEffect, useReducer } from "react";
import jwtDecode from "jwt-decode";
import axios from "axios.js";
import { MatxLoading } from "app/components";
import { _post } from "app/apis/apiMethods";
import { toast } from "react-toastify";


const initialState = {
  isAuthenticated: false,
  isInitialised: false,
  user: null,
};

const isValidToken = (accessToken) => {
  if (!accessToken) {
    return false;
  }
  const decodedToken = jwtDecode(accessToken);
  const currentTime = Date.now() / 1000;

  return decodedToken.exp > currentTime;
};

const setSession = (data) => {
  if (data.accessToken) {
    localStorage.setItem("accessToken", JSON.stringify(data));
    axios.defaults.headers.common.Authorization = `Bearer ${data.accessToken}`;
  } else {
    localStorage.removeItem("accessToken");
    delete axios.defaults.headers.common.Authorization;
  }
};

const reducer = (state, action) => {
  switch (action.type) {
    case "INIT": {
      const { isAuthenticated, user } = action.payload;

      return {
        ...state,
        isAuthenticated,
        isInitialised: true,
        user,
      };
    }
    case "LOGIN": {
      const { user } = action.payload;

      return {
        ...state,
        isAuthenticated: true,
        user,
      };
    }
    case "LOGOUT": {
      return {
        ...state,
        isAuthenticated: false,
        user: null,
      };
    }
    case "REGISTER": {
      const { user } = action.payload;

      return {
        ...state,
        isAuthenticated: true,
        user,
      };
    }
    default: {
      return { ...state };
    }
  }
};

const AuthContext = createContext({
  ...initialState,
  method: "JWT",
  login: () => Promise.resolve(),
  logout: () => { },
  register: () => Promise.resolve(),
});

export const AuthProvider = ({ children }) => {
  const [state, dispatch] = useReducer(reducer, initialState);

  const login = async (credential, password) => {
    const response = await _post(`/user/login`, {
      credential,
      password,
    });
    // const { accessToken } = response.data.data;
    const user = response.data.data;
    const { status } = response.data;

    if (status) {
      setSession(response.data.data);
      toast.success(response.data.message);
      dispatch({
        type: "LOGIN",
        payload: {
          user,
        },
      });
    } else {
      toast.error(response.data.message);
    }
  };

  const register = async (formData) => {
    const response = await _post(`/user/register`, formData);
    const { status } = response.data;
    const { accessToken, user } = response.data.data;

    console.log(user,"user")
    if (status) {
      setSession(accessToken);
      toast.success(response.data.message);
      dispatch({
        type: "REGISTER",
        payload: {
          user,
        },
      });
    } else {
      toast.error(response.data.message);
    }
  };

  const logout = () => {
    setSession(null);
    dispatch({ type: "LOGOUT" });
  };

  useEffect(() => {
    (async () => {
      try {
        const accessToken = window.localStorage.getItem("accessToken");

        if (accessToken && isValidToken(accessToken)) {
          setSession(accessToken);
          // const response = await axios.get("/api/auth/profile");
          // const { user } = response.data;

          const user = {
            "_id": "633e5e0488cd2bd2b9418b50",
            "full_name": "swarnika bhagore",
            "user_name": "swarnika",
            "email": "swarnika@gmail.com",
            "contact_number": 9111970885,
            "profile_image": "/profile_image/1665031684756Screenshot from 2021-11-08 10-23-15.png",
            "otp": "1665031684916",
            "accessToken": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyX25hbWUiOiJzd2FybmlrYSIsImVtYWlsIjoic3dhcm5pa2EiLCJfaWQiOiI2MzNlNWUwNDg4Y2QyYmQyYjk0MThiNTAiLCJpYXQiOjE2NjUwMzMwNDEsImV4cCI6MTY2NTA1MTA0MX0.Ro6PJtO4wAb3hjGjjxzEWlhj8v6vRe6uucidhZlXZEc"

          };

          dispatch({
            type: "INIT",
            payload: {
              isAuthenticated: true,
              user,
            },
          });
        } else {
          dispatch({
            type: "INIT",
            payload: {
              isAuthenticated: false,
              user: null,
            },
          });
        }
      } catch (err) {
        console.error(err);
        dispatch({
          type: "INIT",
          payload: {
            isAuthenticated: false,
            user: null,
          },
        });
      }
    })();
  }, []);

  if (!state.isInitialised) {
    return <MatxLoading />;
  }

  return (
    <AuthContext.Provider
      value={{
        ...state,
        method: "JWT",
        login,
        logout,
        register,
      }}
    >
      {children}
    </AuthContext.Provider>
  );
};

export default AuthContext;
