import React, { createContext, useEffect, useReducer } from "react";
import jwtDecode from "jwt-decode";
import axios from "axios.js";
import { MatxLoading } from "app/components";
import { _post } from "app/apis/apiMethods";
import { toast } from "react-toastify";

const [user,setUser]=useState({});
const initialState = {
  isAuthenticated: false,
  isInitialised: false,
  user: null,
};

const isValidToken = (accessToken) => {
  if (!accessToken) {
    return false;
  }
  const decodedToken = jwtDecode(accessToken);
  const currentTime = Date.now() / 1000;

  return decodedToken.exp > currentTime;
};

const setSession = (accessToken) => {
  if (accessToken) {
    localStorage.setItem("accessToken", accessToken);
    axios.defaults.headers.common.Authorization = `Bearer ${accessToken}`;
  } else {
    localStorage.removeItem("accessToken");
    delete axios.defaults.headers.common.Authorization;
  }
};

const reducer = (state, action) => {
  switch (action.type) {
    case "INIT": {
      const { isAuthenticated, user } = action.payload;

      return {
        ...state,
        isAuthenticated,
        isInitialised: true,
        user,
      };
    }
    case "LOGIN": {
      const { user } = action.payload;

      return {
        ...state,
        isAuthenticated: true,
        user,
      };
    }
    case "LOGOUT": {
      return {
        ...state,
        isAuthenticated: false,
        user: null,
      };
    }
    case "REGISTER": {
      const { user } = action.payload;

      return {
        ...state,
        isAuthenticated: true,
        user,
      };
    }
    default: {
      return { ...state };
    }
  }
};

const AuthContext = createContext({
  ...initialState,
  method: "JWT",
  login: () => Promise.resolve(),
  logout: () => {},
  register: () => Promise.resolve(),
});

export const AuthProvider = ({ children }) => {
  const [state, dispatch] = useReducer(reducer, initialState);

  const login = async (credential, password) => {
    const response = await _post(`/user/login`, {
      credential,
      password,
    });
    const { accessToken } = response.data.data;
     const user = response.data.data;
    const { status } = response.data;

    if (status) {
      setSession(accessToken);
      toast.success(response.data.message);
      dispatch({
        type: "LOGIN",
        payload: {
          user,
        },
      });
    } else {
      toast.error(response.data.message);
    }
  };

  const register = async (formData) => {
    const response = await _post(`/user/register`, formData);

    const { status } = response.data;
    const { accessToken, user } = response.data.data;

    if (status) {
      setSession(accessToken);
      toast.success(response.data.message);
      dispatch({
        type: "REGISTER",
        payload: {
          user,
        },
      });
    } else {
      toast.error(response.data.message);
    }
  };

  const logout = () => {
    setSession(null);
    dispatch({ type: "LOGOUT" });
  };

  useEffect(() => {
    (async () => {
      try {
        const accessToken = window.localStorage.getItem("accessToken");

        if (accessToken && isValidToken(accessToken)) {
          setSession(accessToken);
          // const response = await axios.get("/api/auth/profile");
          // const { user } = response.data;

          const user = {
            _id: "632ae1065a7f9b17a783110a",
            full_name: "aditya vaidya",
            user_name: "adityazws",
            email: "aditya@gmail.com",
            contact_number: 9993680269,
            profile_image:
              "src/uploads/profile_image/1663754502431Screenshot from 2022-09-12 19-22-04.png",
            isAdmin: false,
            createdAt: "2022-09-21T10:01:42.541Z",
            updatedAt: "2022-09-21T10:01:42.541Z",
            __v: 0,
            accessToken:
              "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyX25hbWUiOiJhZGl0eWFAZ21haWwuY29tIiwiZW1haWwiOiJhZGl0eWFAZ21haWwuY29tIiwiX2lkIjoiNjMyYWUxMDY1YTdmOWIxN2E3ODMxMTBhIiwiaWF0IjoxNjY0MTkwMzU2LCJleHAiOjE2NjQyMDgzNTZ9.ML1YwEv0bJeHp4nrUoZ07i60ETTyigckvUlwmRmsIWI",
          };

          dispatch({
            type: "INIT",
            payload: {
              isAuthenticated: true,
              user,
            },
          });
        } else {
          dispatch({
            type: "INIT",
            payload: {
              isAuthenticated: false,
              user: null,
            },
          });
        }
      } catch (err) {
        console.error(err);
        dispatch({
          type: "INIT",
          payload: {
            isAuthenticated: false,
            user: null,
          },
        });
      }
    })();
  }, []);

  if (!state.isInitialised) {
    return <MatxLoading />;
  }

  return (
    <AuthContext.Provider
      value={{
        ...state,
        method: "JWT",
        login,
        logout,
        register,
      }}
    >
      {children}
    </AuthContext.Provider>
  );
};

export default AuthContext;
